// components/Hero.js
import React from 'react';
import './Hero.css';
import { Swiper, SwiperSlide } from 'swiper/react'; // Import Swiper components
import 'swiper/css'; // Import Swiper styles
import 'swiper/css/autoplay';
import { Autoplay } from 'swiper/modules'; // Import Swiper modules

function Hero() {
  const images = [
    'https://www.ndrf.gov.in/sites/default/files/NDRFs%20First%20Mountaineering%20Expedition.jpg', // Image 1
    'https://www.ndrf.gov.in/sites/default/files/Slider%20Landslide_0.png', // Image 2
    'https://example.com/image3.jpg', // Image 3
    // Add more image URLs as needed
  ];

  return (
    <section className="hero">
      <Swiper
        modules={[Autoplay]}
        spaceBetween={0}
        slidesPerView={1}
        loop={true} // Enables infinite loop of slides
        autoplay={{ delay: 5000, disableOnInteraction: false }} // Auto play with 5 seconds delay
      >
        {images.map((image, index) => (
          <SwiperSlide key={index}>
            <div
              className="hero-slide"
              style={{ backgroundImage: `url(${image})` }}
            >
              <div className="hero-overlay">
                <div className="hero-content">
                  <h2>Saving Lives & Beyond</h2>
                  <p>Identify affected individuals swiftly with our advanced technology.</p>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
}

export default Hero;
