  import React, { useState } from 'react';
  import './Contact.css';
  import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'; // Importing Leaflet components
  import 'leaflet/dist/leaflet.css'; // Import Leaflet CSS
  import { IoLocation } from "react-icons/io5";

  function Contact() {
    const [location, setLocation] = useState({ lat: '', lon: '' });

    const handleGetLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            setLocation({
              lat: position.coords.latitude,
              lon: position.coords.longitude,
            });
          },
          (error) => {
            console.error('Error fetching location:', error);
            alert('Unable to retrieve your location.');
          }
        );
      } else {
        alert('Geolocation is not supported by your browser.');
      }
    };

    return (
      <section className="contact" id="contact">
        <h2>Contact Us</h2>
        <p>For assistance, please reach out to our support team.</p>

        <div className="contact-options">
          <form className="contact-form">
            <input type="text" placeholder="Your Name" required />
            <input type="email" placeholder="Your Email" required />
            <input type="tel" placeholder="Your Phone Number" required /> {/* Phone number input field */}
            <textarea placeholder="Your Message" required></textarea>

            {/* Location fields */}
            <input
              type="text"
              placeholder="Latitude"
              value={location.lat}
              readOnly
            />
            <input
              type="text"
              placeholder="Longitude"
              value={location.lon}
              readOnly
            />

            <button type="button" onClick={handleGetLocation}>
              Get Current Location
            </button>

            <button type="submit">Submit</button>
          </form>

          <div className="call-option">
            <p>Or give us a call:</p>
            <a href="tel:+1234567890" className="phone-link">
              +1 (234) 567-890
            </a>
          </div>
        </div>

        {/* Show the map when location is available */}
        {location.lat && location.lon && (
          <div className="map-container">
            <MapContainer
              center={[location.lat, location.lon]}
              zoom={13}
              style={{ height: '400px', width: '100%', marginTop: '20px' }}
            >
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              <Marker position={[location.lat, location.lon]}>
                <Popup>Your Current Location</Popup>
              </Marker>
            </MapContainer>
          </div>
        )}
      </section>
    );
  }

  export default Contact;
